generated.css:
.card {
  background: #fff;
}
.panel {
  background: #fff;
  display: flex;
  flex-direction: column;
  padding: 6px;
  min-width: 100px;
  min-height: 20px;
  border-radius: 1px;
  box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.4);
}
.panel > * {
  margin-top: 5px;
  margin-bottom: 5px;
}
.slider-2d-container {
  display: flex;
  flex-direction: column;
  min-height: 100px;
  min-width: 100px;
  border-radius: 2px;
}
.slider-2d-canvas {
  background: #eee;
  width: 200px;
  height: 100px;
}
.line-editor-canvas {
  background: #eee;
  width: 100%;
  height: 30px;
}
.circular-slider {
  height: auto;
}
.circular-slider-canvas {
  height: auto;
  width: auto;
  min-height: 50px;
}
/*-- Collapsable --*/
.collapsible {
  background-color: #eee;
  color: #111;
  width: 100%;
}
.collapsible .top {
  display: grid;
  width: 100%;
  grid-template-columns: 90% 10%;
  align-items: center;
  background: #eee;
  color: #111;
}
.collapsible .top:hover {
  opacity: 1;
}
.collapsible:hover .arrow {
  opacity: 1;
}
.collapsible .label {
  cursor: pointer;
  width: auto;
  display: flex;
  flex-direction: row;
  justify-content: left;
  padding: 5px;
  align-items: center;
}
.collapsible .arrow {
  display: flex;
  justify-content: center;
  align-content: center;
  height: 22px;
  opacity: 0.5;
  transition: 0.5s;
}
.collapsible .arrow > svg {
  transition: 0.25s;
}
.collapsible .arrow-up > svg {
  transform: rotate(180deg);
}
.collapsible > .content {
  background: #fff;
  transition: 0.1s;
  width: 100%;
  display: block;
  flex-direction: column;
  overflow: scroll;
  justify-content: center;
  border: solid #fff 1px;
  padding: 0px;
  margin: 0px;
}
.collapsible > .content-open {
  height: auto;
  opacity: 1;
  border-bottom: 2px solid #888;
}
.collapsible > .content-closed {
  height: 0px;
  overflow: hidden;
  opacity: 1;
  border-bottom: 2px solid #888;
}

