body{
    padding:0px;
    background:#fff !important;
    font-family: 'Segoe UI', Tahoma;
    font-weight: 100;
    overflow: scroll;
    padding-top:20px;
}

/*---Title bar---*/
.title-bar{
  position: absolute;
  top:0px;
  background:#334;
  left:0px;
  width:100vw;
  height: 20px;
  -webkit-user-select: none;
  -webkit-app-region: drag;
  z-index: 10;
  box-shadow: 0px 2px 10px rgba(0,0,0,0.2);
  display: grid;
  grid-template-columns: 33% 33% 33%;
  font-size: 8pt;
  font-weight: 100;
}

.title-bar > div {
  display: flex;
  flex-direction: row;
  justify-content: center;
  width:100%;
  align-items: center;
  color:#aaa;
}

.title-bar > .group-right{
  justify-content: flex-end;
  padding-right: 8px;
}

.title-bar > .buttons{
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  padding-left:8px;
}

.title-bar > .buttons > div{
  height: 10px;
  width:10px;
  border-radius: 50px;
  margin-right: 5px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.title-bar > .buttons > div > svg{
  opacity: 0;
  transition: 0.5s;
}

.title-bar > .buttons:hover > div > svg{
  opacity: 0.6;
}

.title-bar-close{
  background: #EE5F56;
  border: solid 1px #E03E35;
}

.title-bar-min{
  background:#F8BD45;
  border: solid 1px #F8BD45;
}

.title-bar-max{
  background:#5FCC43;
  border: solid 1px #52AD30;
}

canvas {
    width: 100%;
    height: 100px;
  }

  .annotation {
    position: absolute;
    top: 0;
    left: 0;
    z-index: 1;
    margin-left: 15px;
    margin-top: 15px;
    padding: 1em;
    width: 200px;
    color: #fff;
    background: rgba(0, 0, 0, 0.8);
    border-radius: .5em;
    font-size: 12px;
    line-height: 1.2;
    transition: opacity .5s;
  }
  .annotation::before {
    content: '1';
    position: absolute;
    top: -30px;
    left: -30px;
    width: 30px;
    height: 30px;
    border: 2px solid #fff;
    border-radius: 50%;
    font-size: 16px;
    line-height: 30px;
    text-align: center;
    background: rgba(0, 0, 0, 0.8);
  }

  .env {
    position: absolute;
    z-index: 0;
    margin:0px;
    top:20px;
    left:0px;
  }

  .viewer-helper{
    z-index: 1;
    position: absolute;
    margin:0px;
    top:20px;
    right:0px;

    width:100px;
    height:100px;

    background:transparent;
    opacity: 0.8;
  }

  .viewer-helper:hover{
    opacity: 1;
  }

  /*3d Env Menu Object*/
  .env-menu{
    z-index: 2;
    background:#fff;
    color:#000;
    position: absolute;
    top:50%;
  }

